import React, { useState } from "react";
import BasicCard from './BasicCard'
import  './styles.css'

function DrawThat() {


    return (
        <div>
            <h1 style= {{
                display: 'flex',
                justifyContent: 'center',
                paddingTop: '50px',
                fontSize: '80px',
                backgroundImage: 'linear-gradient(to right, #f9d030, #f62aa0, #b8ee30)', // creates the linear gradient
                WebkitBackgroundClip: 'text', // clips to just text
                WebkitTextFillColor: 'transparent' // fills the gradient to the clip



            }}>Avoid reading your notes, learn with </h1>

            <h2 style= {{
                display: 'flex',
                justifyContent: 'center',
                paddingTop: '50px',
                fontSize: '70px',
                backgroundImage: 'linear-gradient(to right, #f9d030, #f62aa0, #b8ee30)', // creates the linear gradient
                WebkitBackgroundClip: 'text', // clips to just text
                WebkitTextFillColor: 'transparent' // fills the gradient to the clip



            }}>Upload your lecture notes</h2>

            <p style= {{
                display: 'flex',
                justifyContent: 'center',
                backgroundImage: 'linear-gradient(to right, #26dfd0, #b8ee30, #f62aa0, #f9d030',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
            }}> or learn about a topic of your choice </p>
            <BasicCard />

        </div>
    );
}

export default DrawThat;
